<div class="row page-titles">    <div class="col-md-12 align-self-center clearfix">        @if($title)            <h3 class="text-themecolor m-b-0 m-t-0 pull-left">{{ $title }}</h3>        @endif        <ol class="breadcrumb pull-right">            @foreach($breadcrumb as $item)                <li class="breadcrumb-item {{ $item['class'] }}">                    @if(isset($item['route']))                        <a href="{{ route($item['route'], $item['parameters']) }}">{{ $item['text'] }}</a>                    @else                        {{ $item['text'] }}                    @endif                </li>            @endforeach        </ol>    </div></div>